import React, { Component } from "react"
import {Button} from "react-bootstrap"

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      doubleClicked: false
    }
  }

  handleUpdate = () => {
    this.setState({
      doubleClicked: true
    })
    console.log("dateClick")
  }

  popoverClickRootClose = () => {
  <Popover id="popover-trigger-click-root-close" title="Popover bottom">
    <strong>Holy guacamole!</strong> Check this info.
  </Popover>
  }

  render() {
    return (
      <tr onDoubleClick={this.handleUpdate}>
        <td>{this.props.transactionObject.date}</td>
        <td>{this.props.transactionObject.description}</td>
        <td>{this.props.transactionObject.category.name}</td>
        <td>{this.props.transactionObject.amount}</td>
      </tr>
    )
  }
}
