import React, { Component } from "react"
import {Button, Popover, OverlayTrigger, FormGroup, FormControl, Form} from "react-bootstrap"

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      doubleClicked: false
    }
  }

  handleUpdate = () => {
    this.setState({
      doubleClicked: true
    })
    console.log("dateClick")
  }

  render() {
    return (
      this.state.doubleClicked ?
      <tr onDoubleClick={this.handleUpdate}>
        <td>{this.props.transactionObject.date}</td>
        <td>{this.props.transactionObject.description}</td>
        <td>{this.props.transactionObject.category.name}</td>
        <td>{this.props.transactionObject.amount}</td>
      </tr> :
      <tr onDoubleClick={this.handleUpdate}>
      <Form>
        <FormGroup>
          <FormControl
            type="date"
            label="Date"
            placeholder="Date"
            name="date"
            value={this.state.date}
            onChange={this.handleChange}
          />
          <FormControl
            label="Description"
            placeholder="Transaction Description"
            name="description"
            value={this.state.description}
            onChange={this.handleChange}
          />
          {this.props.selectedCategory ? this.props.selectedCategory.name : null}
          <FormControl
            type="number"
            min="0.01"
            step="0.01"
            label="Amount"
            placeholder="Amount"
            name="amount"
            value={this.state.amount}
            onChange={this.handleChange}
          />
        </FormGroup>
        <Button onClick={this.handleSubmit}>Submit</Button>
      </Form>
      </tr>
    )
  }
}
