import React, { Component } from "react"
import {Bar} from 'react-chartjs-2'

export default class CategoryContainerChart extends Component {

  render() {

    let dataObject = {
        type: 'bar',
        data: {
            labels: this.props.categoryLabels,
            datasets: [{
                label: 'Money Spent',
                data: this.props.categoryTotals,
                backgroundColor: [
                    'red',
                    'orange',
                    'yellow',
                    'lime',
                    'green',
                    'cyan',
                    'blue',
                    'purple',
                    'magenta',
                    'grey'
                ],
                borderColor: [
                    'red',
                    'orange',
                    'yellow',
                    'lime',
                    'green',
                    'cyan',
                    'blue',
                    'purple',
                    'magenta',
                    'grey'
                ],
                opacity: [.5],
                borderWidth: 1
            }]
        },
        options: {
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero:true
                    }
                }]
            }
        }
    }

    return (
      <div className="chart">
        <Bar
          data={dataObject.data}
          width={500}
          height={500}
          options={{
            title: {
              display: true,
              text: `Spending Categories in ${this.props.currentMonth}`,
              fontsize: 100
            },
            legend: {
              display: true,
              position: "bottom"
            }
          }}
        />
      </div>
    )
  }
}
