import React, { Component } from "react"
import {Popover, Overlay, Button} from "react-bootstrap"

function CustomPopover({ className, style }) {
  return (
    <div
      className={className}
      style={{
        ...style,
        position: 'absolute',
        backgroundColor: '#EEE',
        boxShadow: '0 5px 10px rgba(0, 0, 0, 0.2)',
        border: '1px solid #CCC',
        borderRadius: 3,
        marginLeft: -5,
        marginTop: 5,
        padding: 10
      }}
    >
      <strong>Holy guacamole!</strong> Check this info.
    </div>
  );
}

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      show: false
    }
  }

  handleToggle = () => {
    this.setState({ show: !this.state.show });
  }

  render() {
    return (
      <tr>
        <td>{this.props.transactionObject.date}</td>
        <td>{this.props.transactionObject.description}</td>
        <td>{this.props.transactionObject.category.name}</td>
        <td>{this.props.transactionObject.amount}</td>
        <div style={{ height: 100, position: 'relative' }}>
          <Button
            ref={button => {
              this.target = button;
            }}
            onClick={this.handleToggle}
          >
            I am an Overlay target
          </Button>

          <Overlay
            show={this.state.show}
            onHide={() => this.setState({ show: false })}
            placement="right"
            container={this}
            target={() => React.findDOMNode(this.target)}
          >
            <CustomPopover />
          </Overlay>
        </div>
      </tr>
    )
  }
}
