import React, { Component } from "react"
import {Popover, Overlay, Button} from "react-bootstrap"

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      show: false
    }
  }

  handleToggle = () => {
    this.setState({ show: !this.state.show });
  }

  render() {
    return (
      <tr>
        {!this.state.show ?
        <td>{this.props.transactionObject.date}</td> :
        <td><input type="date" name="date" value={this.props.transactionObject.date}/></td>}

        {!this.state.show ?
        <td>{this.props.transactionObject.description}</td> :
        <td><input type="text" name="description" value={this.props.transactionObject.description}/></td>
        }

        <td>{this.props.transactionObject.category.name}</td>

        {!this.state.show ?
        <td>{this.props.transactionObject.amount}</td> :
        <td><input type="number" name="amount" min="0.01" step="0.01" value={this.props.transactionObject.date}/></td>
        }

        <td>
          <Button
            ref={button => {
              this.target = button;
            }}
            onClick={this.handleToggle}
          >
            Update
          </Button>
        </td>
      </tr>
    )
  }
}
