import React, { Component } from "react"
import Transactions from "./Transactions"

export default class CategoryBar extends Component {

  render() {
    let categoryTransactions = this.props.transactions.filter(transaction => transaction.category.name === this.props.category)

    let categoryTotal = categoryTransactions.map(transaction => transaction.amount)

    let reducer = (accumulator, currentValue) => accumulator + currentValue
    categoryTotal = categoryTotal.reduce(reducer)

    return (
      <div>
        {this.props.category}
      </div>
    )
  }
}
