import React, { Component } from "react"
import {Popover, Overlay, Button} from "react-bootstrap"

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      show: false,
      initialValues: {
        date: this.props.transactionObject.date,
        description: this.props.transactionObject.description,
        category: this.props.transactionObject.category.name,
        amount: this.props.transactionObject.amount
      }
    }
  }

  handleToggle = () => {
    this.setState({ show: !this.state.show });
  }

  render() {
    return (
      <tr>
        {!this.state.show ?
        <td>{this.state.initialValues.date}</td> :
        <td><input type="date" name="date" placeholder={this.state.initialValues.date} value={this.state.initialValues.date}/></td>}

        {!this.state.show ?
        <td>{this.state.initialValues.description}</td> :
        <td>
        <input
          type="text"
          name="description"
          placeholder={this.state.initialValues.description} value={this.state.initialValues.description}
        />
        </td>
        }

        <td>{this.state.initialValues.category.name}</td>

        {!this.state.show ?
        <td>{this.state.initialValues.amount}</td> :
        <td><input
          type="number"
          name="amount"
          min="0.01"
          placeholder={this.state.initialValues.description}
          value={this.state.initialValues.amount}
        />
        </td>
        }

        <td>
          <Button
            ref={button => {
              this.target = button;
            }}
            onClick={this.handleToggle}
          >
            Update
          </Button>
        </td>
      </tr>
    )
  }
}
