import React, { Component } from "react"
import {Popover, Overlay, Form, FormGroup, FormControl, Button} from "react-bootstrap"

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      show: false,
      date: "",

    }
  }

  getInitialState = () => {

  }

  handleToggle = () => {
    this.setState({ show: !this.state.show });
  }

  handleChange = (event) => {
    const name = event.target.name
    const value = event.target.value
    this.setState({[name]: value})
  }

  render() {
    return (
      <tr>
        {!this.state.show ?
        <td>{this.props.transactionObject.date}</td> :
        <td>
        <FormControl
          type="date"
          label="Date"
          placeholder="Date"
          name="date"
          value={this.props.transactionObject.date}
          onChange={this.handleChange}
        />
        </td>}

        {!this.state.show ?
        <td>{this.props.transactionObject.description}</td> :
        <td>
        <FormControl
          type="text"
          label="Description"
          placeholder="description"
          name="description"
          value={this.props.transactionObject.description}
          onChange={this.handleChange}
        />
        </td>
        }

        <td>{this.props.transactionObject.category.name}</td>

        {!this.state.show ?
        <td>{this.props.transactionObject.amount}</td> :
        <td>
        <FormControl
          type="number"
          label="Amount"
          placeholder="Amount"
          name="amount"
          value={this.props.transactionObject.amount}
          onChange={this.handleChange}
        />
        </td>
        }

        <td>
          <Button
            ref={button => {
              this.target = button;
            }}
            onClick={this.handleToggle}
          >
            Update
          </Button>
        </td>
      </tr>
    )
  }
}
