import React, { Component } from "react"
import {Popover, Overlay} from "react-bootstrap"

export default class Transactions extends Component {

  constructor() {
    super()
    this.state = {
      show: false
    }
  }

  handleToggle = () => {
    this.setState({ show: !this.state.show });
  }

  customPopover = ({ className, style }) => {
    return (
      <div
        className={className}
        style={{
          ...style,
          position: 'absolute',
          backgroundColor: '#EEE',
          boxShadow: '0 5px 10px rgba(0, 0, 0, 0.2)',
          border: '1px solid #CCC',
          borderRadius: 3,
          marginLeft: -5,
          marginTop: 5,
          padding: 10
        }}
      >
        <strong>Holy guacamole!</strong> Check this info.
      </div>
    );
  }

  render() {
    return (
      <Overlay trigger="click" placement="bottom" overlay={this.handleUpdate}>
        <tr>
          <td>{this.props.transactionObject.date}</td>
          <td>{this.props.transactionObject.description}</td>
          <td>{this.props.transactionObject.category.name}</td>
          <td>{this.props.transactionObject.amount}</td>
        </tr>
      </Overlay>
    )
  }
}
